import React, { useEffect } from 'react';
import { LinkContainer } from 'react-router-bootstrap';
import { Table, Button, Row, Col } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import Message from '../../components/message/message.component';
import Loader from '../../components/loader/loader.component';
import Paginate from '../../components/paginate/paginate.component';

import { 
    listProducts, 
    deleteProduct,
    createProduct 
} from '../../redux/product/product.actions';

import ProductActionTypes from '../../redux/product/product.types';

const ProductListPage = ({ history, match }) => {
    const pageNumber = match.params.pageNumber || 1;

    const dispatch = useDispatch();

    const productList = useSelector(state => state.productList);
    const { loading, error, products, page, pages } = productList;

    const userLogin = useSelector(state => state.userLogin);
    const { userInfo } = userLogin;

    const productDelete = useSelector(state => state.productDelete);
    const { 
        loading: loadingDelete, 
        error: errorDelete, 
        success: successDelete 
    } = productDelete;

    const productCreate = useSelector(state => state.productCreate);
    const { 
        loading: loadingCreate, 
        error: errorCreate, 
        success: successCreate ,
        product: createdProduct
    } = productCreate;


    useEffect(() => {
        dispatch({ type: ProductActionTypes.PRODUCT_CREATE_RESET })
        if (!userInfo.isAdmin){
            history.push('/login');
            
        } 

        if (successCreate) {
            history.push(`/admin/product/${createdProduct.data._id}/edit`)
        } else {
            //^ pass empty string in as keyword so the admin page is not affected.
            dispatch(listProducts('', pageNumber));
        };

    }, [dispatch, history, userInfo, successDelete, successCreate, createdProduct, pageNumber]);

    const deleteHandler = (id) => {
        if (window.confirm('Are you sure')){
            dispatch(deleteProduct(id));
        };
    };

    const createProductHandler = (product) => {
        dispatch(createProduct());
    };

    return (
        <>
            <Row className='align-items-center'>
                <Col>
                    <h1>Products</h1>
                </Col>
                <Col className='text-end' >
                    <Button
                        className='my-3'
                        onClick={createProductHandler}
                    >
                        <i className='fas fa-plus'/> 
                        &nbsp;&nbsp;Create Product
                    </Button>
                </Col>
            </Row>
            {loadingDelete && <Loader/>}
            {errorDelete && <Message variant='danger' >{errorDelete}</Message>}
            {loadingCreate && <Loader/>}
            {errorCreate && <Message variant='danger' >{errorCreate}</Message>}
            {
                loading 
                ? <Loader/> 
                : error
                ? <Message variant='danger'>{error}</Message>
                : (
                    <>
                    <Table 
                        striped 
                        bordered 
                        hover
                        responsive
                        className='table-sm' 
                    >
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>NAME</th>
                                <th>PRICE</th>
                                <th>CATEGORY</th>
                                <th>BRAND</th>
                            </tr>
                        </thead>
                        <tbody>
                            { products.map( product => (
                                <tr key={product._id}>
                                    <td>{product._id}</td>
                                    <td>{product.name}</td>
                                    <td>${product.price}</td>
                                    <td>{product.category}</td>
                                    <td>{product.brand}</td>
                                    <td>
                                        <LinkContainer to={`/admin/product/${product._id}/edit`}>
                                            <Button variant='light' className='btn-sm'>
                                                <i className='fas fa-edit'></i>
                                            </Button>
                                        </LinkContainer>
                                        <Button 
                                            variant='danger' 
                                            className='btn-sm'
                                            onClick={() => deleteHandler(product._id)}
                                        >
                                            <i className='fas fa-trash'/>
                                        </Button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                    <Paginate pages={pages} page={page} isAdmin={true} />
                    </>
                )
            };
        </>
    );
};

export default ProductListPage
