import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';

import { Row, Col, Image, ListGroup, Card, Button, Form } from 'react-bootstrap';
import Rating from '../../components/rating/rating.component';
import Loader from '../../components/loader/loader.component';
import Message from '../../components/message/message.component';

import { listProductDetails, createProductReview } from '../../redux/product/product.actions';
import ProductActionTypes from '../../redux/product/product.types';

const ProductPage = ({ match, history }) => {
    const [qty, setQty] = useState(1);
    const [rating, setRating] = useState(0);
    const [comment, setComment] = useState('')

    const dispatch = useDispatch();

    const productDetails = useSelector(state => state.productDetails);
    const { loading, error, product } = productDetails

    const productReview = useSelector(state => state.productReview);
    const { success: successProductReview, error: errorProductReview } = productReview

    const userLogin = useSelector(state => state.userLogin);
    const { userInfo } = userLogin

    useEffect(() => {
        if (successProductReview) {
            alert('Review Submitted!');
            setRating(0);
            setComment('');
            dispatch(({ type: ProductActionTypes.PRODUCT_CREATE_REVIEW_RESET }))
        }

        dispatch(listProductDetails(match.params.productId))
    }, [dispatch, match.params.productId, successProductReview]);

    const addToCartHandler = () => {
        history.push(`/cart/${match.params.productId}?qty=${qty}`)
    };

    const submitHandler = (e) => {
        e.preventDefault();
        dispatch(createProductReview(match.params.productId, {
            rating,
            comment
        }));
    };

    return (
        <>
            <Link className='btn btn-light my-3' to='/'>
                Go Back
            </Link>
            { loading 
            ? <Loader/> 
            : error 
            ? <Message variant='danger'>{error}</Message> 
            : !product
            ?  null
            :
            (
            <>
                <Row>
                    <Col md={6}>
                        <Image src={product.image} alt={product.name} fluid/>
                    </Col>
                    <Col md={3}>
                        <ListGroup variant='flush'>
                            <ListGroup.Item>
                                <h3>{product.name}</h3>
                            </ListGroup.Item>
                            <ListGroup.Item>
                                <Rating value={product.rating} text={`${product.numReviews} reviews`}/>
                            </ListGroup.Item>
                            <ListGroup.Item>
                                Price: ${product.price}
                            </ListGroup.Item>
                            <ListGroup.Item>
                                Description: ${product.description}
                            </ListGroup.Item>
                        </ListGroup>
                    </Col>
                    <Col md={3}>
                        <Card>
                            <ListGroup variant='flush'>
                                <ListGroup.Item>
                                    <Row>
                                        <Col>
                                            Price:
                                        </Col>
                                        <Col>
                                            <strong>{product.price}</strong>
                                        </Col>
                                    </Row>
                                </ListGroup.Item>
                                <ListGroup.Item>
                                    <Row>
                                        <Col>
                                            Status:
                                        </Col>
                                        <Col>
                                            {product.countInStock > 0 ? 'In Stock' : 'Out Of Stock'}
                                        </Col>
                                    </Row>
                                </ListGroup.Item>

                                {
                                    product.countInStock > 0 && (
                                        <ListGroup.Item>
                                            <Row>
                                                <Col>Qty</Col>
                                                <Col>
                                                    <Form.Control 
                                                        as='select'
                                                        value={qty}
                                                        onChange={(e) => {
                                                            setQty(e.target.value)
                                                        }}
                                                    >
                                                        {[...Array(Number(product.countInStock)).keys()].map(x => (
                                                            <option key={x + 1} value={ x + 1 }>
                                                                {x + 1}
                                                            </option>
                                                        ))}
                                                    </Form.Control>
                                                </Col>
                                            </Row>
                                        </ListGroup.Item>
                                    )
                                }  

                                <ListGroup.Item>
                                    <Button 
                                        onClick={addToCartHandler}
                                        className='col-12' 
                                        type='button' 
                                        disabled={product.countInStock === 0}
                                    >
                                        Add To Cart
                                    </Button>
                                </ListGroup.Item>
                            </ListGroup>
                        </Card>
                    </Col>
                </Row>
                <Row>
                    <Col md={6}>
                        <h2>Reviews</h2>
                        {product.reviews.length === 0 && <Message>No Reviews</Message>}
                        <ListGroup variant='flush'>
                            {product.reviews.map((review) => (
                                <ListGroup.Item key ={review._id}>
                                    <strong>{review.name}</strong>
                                    <Rating value={review.rating}/>
                                    <p>{review.createdAt.substring(0,10)}</p>
                                    <p>{review.comment}</p>
                                </ListGroup.Item>
                            ))}
                            <ListGroup.Item>
                                <h2>Write a Customer Review</h2>
                                {errorProductReview && <Message variant='danger'>{errorProductReview}</Message>}
                                {userInfo ? (
                                    <Form onSubmit={submitHandler}>

                                        <Form.Group controlId='rating'>
                                            <Form.Label>Rating</Form.Label>
                                            <Form.Control 
                                                as='select' 
                                                value={rating}
                                                onChange={(e) => setRating(e.target.value)}
                                            >
                                                <option value=''>Select...</option>
                                                <option value='1'>1 - Poor</option>
                                                <option value='2'>2 - Fair</option>
                                                <option value='3'>3 - Good</option>
                                                <option value='4'>4 - Very Good</option>
                                                <option value='5'>5 - Excellent</option>
                                            </Form.Control>
                                        </Form.Group>

                                        <Form.Group controlId='comment'>
                                            <Form.Label>Comment</Form.Label>
                                            <Form.Control 
                                                as='textarea' 
                                                row='3'
                                                value={comment}
                                                onChange={(e) => setComment(e.target.value)}
                                            />
                                        </Form.Group>

                                        <Button type='submit' variant='primary' className='my-3'>Submit</Button>

                                    </Form>
                                ) : <Message>Please <Link to='/login'>Sign In</Link> to write a review.</Message> }
                            </ListGroup.Item>
                        </ListGroup>
                    </Col>
                </Row>
            </>
            )}
        </>
    );
};

export default ProductPage;
